mapper.select:
SELECT s.id s_id,stu.*,profess_class.class_name pc_name,insvolunteer.* FROM student s LEFT JOIN ( SELECT u.*, d.id d3_id,d.dept_name d3_name FROM user u LEFT JOIN dept d ON u.dept_id = d.id
WHERE d.id=#{deptId}
) stu ON stu.id = s.id LEFT JOIN profession_class profess_class ON s.class_id = profess_class.id LEFT JOIN ( SELECT iv.id insVolunteer_id,iv.status insVolunteer_status,iv.stu_id stu_id, iv.create_time insVolunteer_create_time, i.id iid, i.type itype, i.name iname FROM ins_voluntee iv LEFT JOIN ( SELECT * FROM internship i
WHERE i.type = "跟岗"
WHERE i.type = "顶岗"
) i ON iv.ins_id = i.id
WHERE iv.status = 0
WHERE iv.status = 1
WHERE iv.status = 2
) insvolunteer ON insvolunteer.stu_id = s.id WHERE insvolunteer.insVolunteer_id IS NOT NULL AND insvolunteer.iid IS NOT NULL
AND profess_class.id= #{professClassId}
AND insvolunteer.iid = #{internshipId}
